typedef struct odbmdl {
     short datano;              /* データの種類 */
     short type;                /* 指令されたブロック */
     union {
          char  g_data;         /* G コードのモーダルデータ */
          char  g_rdata[35];    /* G コードのモーダルデータ */
          char  g_1shot[4];/* 1 shot G コードのモーダルデータ*/
          struct {
                long    aux_data;
                        /* G コード以外のモーダルデータ */
                char    flag1;  /* フラグ 1 */
                char    flag2;  /* フラグ 2 */
          }aux;
          struct {
                long    aux_data;
                        /* G コード以外のモーダルデータ */
                char    flag1;  /* フラグ 1 */
                char    flag2;  /* フラグ 2 */
          }raux1[27];
          struct {
                long    aux_data;
                        /* G コード以外のモーダルデータ */
                char    flag1;  /* フラグ 1 */
                char    flag2;  /* フラグ 2 */
          }raux2[MAX_AXIS];     /* MAX_AXIS : 最大制御軸数 */
     }modal;
} ODBMDL ;

