typedef struct odbcmd {
    char  adrs ;            /* 指令アドレス */
    char  num ;             /* Mコード番号、または軸番号 */
    short flag ;            /* 付加情報 */
    long  cmd_val ;         /* 指令値データ */
    long  dec_val ;         /* 指令値の小数点以下桁数 */
} ODBCMD ;

