content.pre:
typedef struct  iodbto {
    short datano_s;             /* 開始オフセット番号 */
    short type;                 /* オフセットのタイプ */
    short datano_e;             /* 終了オフセット番号 */
    union {
        long    m_ofs[N];       /* M系個別 */
        long    m_ofs_a[N];     /* M系メモリA全体 */
        long    m_ofs_b[2*N];   /* M系メモリB全体 */
        long    m_ofs_c[4*N];   /* M系メモリC全体 */
        struct  {
            short   tip ;
            long    data[1] ;
        } m_ofs_at[N] ;         /* M系メモリA全体 + 仮想刃先方向 */
        struct  {
            short   tip ;
            long    data[2] ;
        } m_ofs_bt[N] ;         /* M系メモリB全体 + 仮想刃先方向 */
        struct  {
            short   tip ;
            long    data[4] ;
        } m_ofs_ct[N] ;         /* M系メモリC全体 + 仮想刃先方向 */
        short   t_tip[N];       /* T系個別仮想刃先方向 */
        long    t_ofs[N];       /* T系個別 */
        struct  {
            short   tip;
            long    data[4];
        } t_ofs_a[N];           /* T系メモリA全体 */
        struct {
            short   tip;
            long    data[8];
        } t_ofs_b[N];           /* T系メモリB全体 */
        long    t_ofs_2g[3*N];  /* T系第2形状全体 */
    } u ;
} IODBTO ;      /* N は読み出すオフセット量の個数 */

vc.pre:
以下のプログラムは旋盤系(メモリB/64組)の全工具補正量を読みだして表示します。

#include "fwlib32.h"
#define MAXTOOL 64

short example( void )
{
    IODBTO *tofsr ;
    short ret, idx ;
    tofsr = (IODBTO *)malloc( 8+36*MAXTOOL ) ;
    ret = cnc_rdtofsr( h, 1, -2, MAXTOOL, 8+36*MAXTOOL, tofsr ) ;
    if ( !ret ) {
        printf( "No  X:wear  Z:wear  Y:wear  X:geom  "
                "Z:geom  Y:geom  R:wear  R:geom T\n" ) ;
        printf( "---+-------+-------+-------+-------+"
                "-------+-------+-------+------+-\n" ) ;
        for ( idx = 0 ; idx < MAXTOOL ; idx++ ) {
            printf( "%02d%8ld%8ld%8ld%8ld%8ld%8ld%8ld%8ld%2d\n",
                    idx, tofsr->u.t_ofs_b[idx].data[4],
                    tofsr->u.t_ofs_b[idx].data[5],
                    tofsr->u.t_ofs_b[idx].data[6],
                    tofsr->u.t_ofs_b[idx].data[0],
                    tofsr->u.t_ofs_b[idx].data[1],
                    tofsr->u.t_ofs_b[idx].data[2],
                    tofsr->u.t_ofs_b[idx].data[7],
                    tofsr->u.t_ofs_b[idx].data[3],
                    tofsr->u.t_ofs_b[idx].tip ) ;
        }
    }
    free( tofsr ) ;
    return ( ret ) ;
}
