#if defined (PMCMEMD)
typedef struct iodbpmc {
    short           type_a ;   /* PMCアドレスの種類 */
    short           type_d ;   /* PMCデータの型 */
    unsigned short  datano_s ; /* 入力開始PMCアドレス */
    unsigned short  datano_e ; /* 入力終了PMCアドレス */
    union {
        char    cdata[N] ; /* PMCデータ(バイト型) */
        short   idata[N] ; /*          (ワード型) */
        long    ldata[N] ; /*          (ロング型) */
    } u ;             /* N は読み取るデータの個数 */
} IODBPMC ;
#else
